// Helper function to extract specific tags using regex
const extractTagContent = (html, tag) => {
  const regex = new RegExp(`<${tag}[^>]*>(.*?)</${tag}>`, 'gi');
  const matches = html.match(regex) || [];
  return matches.map(match => match.replace(/<[^>]*>/g, '').trim());
};

// Helper function to extract meta tags
const extractMetaContent = (html, metaName) => {
  const regex = new RegExp(`<meta name=["']${metaName}["'] content=["'](.*?)["']`, 'i');
  const match = html.match(regex);
  return match ? match[1] : 'Not Found';
};

// Main processing function
const processItem = (item) => {
  const rawHtml = item.json.data; // Access raw HTML from "data" key

  if (!rawHtml) {
    throw new Error('No HTML content found in input data.');
  }

  return {
    metaTitle: extractTagContent(rawHtml, 'title')[0] || 'Not Found',
    metaDescription: extractMetaContent(rawHtml, 'description'),
    h1Heading: extractTagContent(rawHtml, 'h1')[0] || 'Not Found',
    h2Headings: extractTagContent(rawHtml, 'h2'),
    h3Headings: extractTagContent(rawHtml, 'h3'),
  };
};

