// Process product descriptions and format them to clean, structured markdown
for (const item of $input.all()) {
  let productText = item["Final Product Page"];

  if (productText && typeof productText === 'string') {
    // Step 1: If this is a JSON string, parse it
    if (productText.startsWith('"') && productText.endsWith('"')) {
      try {
        productText = JSON.parse(productText);
      } catch (e) {
        // Manual cleanup if JSON parsing fails
        productText = productText
          .substring(1, productText.length - 1)
          .replace(/\\n/g, "\n")
          .replace(/\\"/g, '"')
          .replace(/\\\\/g, "\\");
      }
    }

    // Step 2: Create a new field with HTML representation for display
    // This will allow n8n to show it properly instead of the escaped version
    const htmlOutput = productText
      .replace(/\n/g, "<br>")                 // Convert newlines to HTML breaks
      .replace(/## (.*?)$/gm, "<h2>$1</h2>")  // Format h2 headings
      .replace(/### (.*?)$/gm, "<h3>$1</h3>") // Format h3 headings
      .replace(/\*\*(.*?)\*\*/g, "<strong>$1</strong>") // Bold text
      .replace(/- (.*?)$/gm, "â€¢ $1<br>")      // Format bullet points
      .replace(/  /g, "&nbsp;&nbsp;");        // Preserve spaces

    // Add a new field for the HTML view
    item["Product Page HTML"] = htmlOutput;

    // Also keep a clean markdown version with proper newlines
    item["Clean Markdown"] = productText;

    // Step 3: Update the original field with the cleaned text 
    // (this will still show as JSON in n8n's interface)
    item["Final Product Page"] = productText;
  }
}

return $input.all();

// For n8n: Extract only the HTML content from the JSON
function processItem(item) {
  try {
    // If the item already has htmlContent field, use it directly
    if (item.json.htmlContent) {
      item.json = { html: item.json.htmlContent };
    } 
    // Convert markdown from 'output' field
    else if (item.json.output) {
      const markdownContent = item.json.output;
      item.json = { html: markdownToHtml(markdownContent) };
    } 
    else {
      item.json = { error: "No htmlContent or output field found" };
    }
  } catch (error) {
    item.json = { error: `Error processing HTML: ${error.message}` };
  }
  return item;
}

// Markdown to HTML conversion function
function markdownToHtml(markdown) {
  if (!markdown) return '';

  // 1. Convert Markdown headers (H1 to H6)
  let html = markdown
    .replace(/^###### (.*?)$/gm, '<h6>$1</h6>')
    .replace(/^##### (.*?)$/gm, '<h5>$1</h5>')
    .replace(/^#### (.*?)$/gm, '<h4>$1</h4>')
    .replace(/^### (.*?)$/gm, '<h3>$1</h3>')
    .replace(/^## (.*?)$/gm, '<h2>$1</h2>')
    .replace(/^# (.*?)$/gm, '<h1>$1</h1>');

  // 2. Convert bold and italic markdown
  html = html
    .replace(/\*\*(.*?)\*\*/g, '<strong>$1</strong>') // Bold
    .replace(/\*(.*?)\*/g, '<em>$1</em>');            // Italic

  // 3. Handle unordered lists with support for nesting
  const lines = html.split('\n');
  let processedLines = [];
  let inList = false;
  let previousIndentLevel = 0;
  let listStack = [];

  for (let i = 0; i < lines.length; i++) {
    let line = lines[i];
    const listMatch = line.match(/^(\s*)- (.*?)$/);

    if (listMatch) {
      const [fullMatch, indent, content] = listMatch;
      const indentLevel = Math.floor(indent.length / 2);

      if (!inList) {
        processedLines.push('<ul>');
        listStack.push('</ul>');
        inList = true;
      }

      if (indentLevel > previousIndentLevel) {
        processedLines[processedLines.length - 1] = processedLines[processedLines.length - 1].replace('</li>', '');
        processedLines.push('<ul>');
        listStack.push('</ul></li>');
      } else if (indentLevel < previousIndentLevel) {
        while (previousIndentLevel > indentLevel) {
          processedLines.push(listStack.pop());
          previousIndentLevel--;
        }
      }

      processedLines.push(`<li>${content}</li>`);
      previousIndentLevel = indentLevel;
    } else {
      if (inList) {
        while (listStack.length > 0) {
          processedLines.push(listStack.pop());
        }
        inList = false;
        previousIndentLevel = 0;
      }
      processedLines.push(line);
    }
  }

  if (inList) {
    while (listStack.length > 0) {
      processedLines.push(listStack.pop());
    }
    inList = false;
  }

  html = processedLines.join('\n');

  // 4. Wrap remaining lines in <p> tags (paragraphs)
  const paragraphLines = html.split('\n');
  let inParagraph = false;
  let result = [];

  for (let i = 0; i < paragraphLines.length; i++) {
    let line = paragraphLines[i].trim();

    if (!line) {
      if (inParagraph) {
        result[result.length - 1] += '</p>';
        inParagraph = false;
      }
      continue;
    }

    if (line.startsWith('<')) {
      if (inParagraph) {
        result[result.length - 1] += '</p>';
        inParagraph = false;
      }
      result.push(line);
      continue;
    }

    if (!inParagraph) {
      result.push('<p>' + line);
      inParagraph = true;
    } else {
      result[result.length - 1] += '<br>' + line;
    }
  }

  if (inParagraph) {
    result[result.length - 1] += '</p>';
  }

  html = result.join('\n');

  // 5. Clean-up
  html = html
    .replace(/<p><\/p>/g, '') // Remove empty <p>
    .replace(/<p>(<h[1-6]>.*?<\/h[1-6]>)<\/p>/g, '$1') // Remove <p> around headers
    .replace(/<p>(<ul>.*?<\/ul>)<\/p>/gs, '$1'); // Remove <p> around lists

  return html;
}

