// Process product descriptions and format them to clean, structured markdown
for (const item of $input.all()) {
  let productText = item["Final Product Page"];

  if (productText && typeof productText === 'string') {
    // Step 1: If this is a JSON string, parse it
    if (productText.startsWith('"') && productText.endsWith('"')) {
      try {
        productText = JSON.parse(productText);
      } catch (e) {
        // Manual cleanup if JSON parsing fails
        productText = productText
          .substring(1, productText.length - 1)
          .replace(/\\n/g, "\n")
          .replace(/\\"/g, '"')
          .replace(/\\\\/g, "\\");
      }
    }

    // Step 2: Create a new field with HTML representation for display
    // This will allow n8n to show it properly instead of the escaped version
    const htmlOutput = productText
      .replace(/\n/g, "<br>")                 // Convert newlines to HTML breaks
      .replace(/## (.*?)$/gm, "<h2>$1</h2>")  // Format h2 headings
      .replace(/### (.*?)$/gm, "<h3>$1</h3>") // Format h3 headings
      .replace(/\*\*(.*?)\*\*/g, "<strong>$1</strong>") // Bold text
      .replace(/- (.*?)$/gm, "‚Ä¢ $1<br>")      // Format bullet points
      .replace(/  /g, "&nbsp;&nbsp;");        // Preserve spaces

    // Add a new field for the HTML view
    item["Product Page HTML"] = htmlOutput;

    // Also keep a clean markdown version with proper newlines
    item["Clean Markdown"] = productText;

    // Step 3: Update the original field with the cleaned text 
    // (this will still show as JSON in n8n's interface)
    item["Final Product Page"] = productText;
  }
}

return $input.all();

