// Get data from the Entity Extractor Agent node
const data = $input.all().map(item => {
  return {
    json: {
      ImagePrompts: item.json.output // Rename 'output' to 'entityExtractedText'
    },
    pairedItem: 0
  };
});

return data;

// Assume input is in the format: [{ output: "```json\n{...}\n```" }]
const raw = items[0].json.output;

// Extract and clean the JSON block
const jsonString = raw.replace(/^```json\n/, "").replace(/\n```$/, "");
const data = JSON.parse(jsonString);

// Flatten top-level fields
const result = {
  ArticleTitle: data.ArticleTitle,
  SearchIntent: data.SearchIntent,
  TargetAudience: data.TargetAudience,
  ArticleLength: data.ArticleLength,
  ContentPersona: data.ContentPersona,
  WritingStyle: data.WritingStyle,
  ToneOfVoice: data.ToneOfVoice,
  ArticleComplexity: data.ArticleComplexity,
  TechnicalLevel: data.TechnicalLevel,
  ArticleTypeDetected: data.ArticleTypeDetected,
  ContentTopics: data.ContentTopics.join("; "),
  KeywordsToInclude: data.KeywordsToInclude.join("; "),
  RelatedKeywordsUsed: data.RelatedKeywordsUsed.map(k => k.keyword).join("; "),
  SuggestedSections: data.SuggestedSections.join("; ")
};

// Flatten ArticleTypeEvidence
result.ArticleTypeEvidence = data.ArticleTypeEvidence
  .map(e => `${e.title} => ${e.matchedType} [${e.structuralClues}]`)
  .join("; ");

// Flatten SERPInsights subfields
const insights = data.SERPInsights;
result.DomainFrequency = JSON.stringify(insights.DomainFrequency);
result.TopDomains = insights.TopDomains.join("; ");
result.CommunityContentPresence = insights.CommunityContentPresence;
result.YouTubePresence = insights.YouTubePresence;
result.QuestionBasedTitles = insights.QuestionBasedTitles;
result.DeclarativeTitles = insights.DeclarativeTitles;
result.ContentFormatsDetected = insights.ContentFormatsDetected.join("; ");
result.ContentTypeDistribution = JSON.stringify(insights.ContentTypeDistribution);
result.ContentFreshness_RecentArticles = insights.ContentFreshness.RecentArticles;
result.ContentFreshness_OlderThan2Years = insights.ContentFreshness.OlderThan2Years;
result.MissingFormats = insights.OpportunityInsights.MissingFormats.join("; ");
result.UnderservedIntent = insights.OpportunityInsights.UnderservedIntent
  .map(i => `${i.intent} (missed by: ${i.missedBy.join(", ")})`)
  .join("; ");
result.RecommendedFormatType = insights.RecommendedFormatStrategy.type;
result.RecommendedFormatReason = insights.RecommendedFormatStrategy.reason;

// Return as single row
return [{ json: result }];
